import { useState } from 'react';
import './Contact.css';
import img_ico from '../../assets/ico/list_img_ico';
import emailjs from 'https://cdn.jsdelivr.net/npm/@emailjs/browser@3/dist/email.min.js';


function Contact (props){

    const [value, setValue] = useState({nombre: '', correo: '', mensaje:''});

    const handleChange = (e) => {
        setValue((state)=>({
            ...state,
            [e.target.name] : e.target.value
        }));
    }

    const btn = document.getElementById('button');

    document.getElementById('form')
    .addEventListener('submit', function(event) {
    event.preventDefault();

    btn.value = 'Sending...';

    const serviceID = 'default_service';
    const templateID = 'template_b40hr0b';

    emailjs.sendForm(serviceID, templateID, this)
        .then(() => {
        btn.value = 'Send Email';
        alert('Sent!');
        }, (err) => {
        btn.value = 'Send Email';
        alert(JSON.stringify(err));
        });
    });


    console.log(value);

    return(
        <section className={`section-contact section-applies ${props.page === 5 ? "active" : null}`}>

            <form className='from-box'>
                <div className='form-banner'>
                    <span>Contactame</span>
                    <p>
                    Completa el formulario y estaremos en contacto, tambi√©n puedes hablarme en mis redes sociales.
                    </p>
                    <img src='https://static.vecteezy.com/system/resources/thumbnails/004/579/307/small_2x/programmer-working-on-computer-with-lots-of-data-free-vector.jpg' alt=''/>
                </div>

                <div className='form'>
                    <span>Tu nombre</span>
                    <div className='input-box'>
                        <img src={`${img_ico[7].img}`} alt=''/>
                        <input
                        id="from_name" 
                        type='text'
                        name='nombre'
                        value={value.nombre}
                        autoComplete='off'
                        onChange={handleChange}
                        />
                    </div>

                    <span>Correo</span>
                    <div className='input-box'>
                        <img src={`${img_ico[6].img}`} alt=''/>
                        <input
                        id="email_id"
                        type='text'
                        name='correo'
                        value={value.correo}
                        autoComplete='off'
                        onChange={handleChange}
                        />
                    </div>


                    <span>Mensaje</span>
                    <div className='input-box'>
                        <textarea
                        id="message"
                        type='text'
                        name='mensaje'
                        value={value.mensaje}
                        autoComplete='off'
                        onChange={handleChange}
                        />
                    </div>

                    <input 
                    type="submit"
                    className='btn-enviar' 
                    id="button"
                    Enviar
                    />
                </div>

            </form>

        </section>
    );
}

export {Contact};
