import { useState } from 'react';
import './Contact.css';


function Contact (props){

    const [value, setValue] = useState({nombre: '', correo: '', mensaje:''});

    const handleChange = (e) => {
        setValue((state)=>({
            ...state,
            [e.target.name] : e.target.value
        }));
    }

    console.log(value);

    return(
        <section className={`section-contact ${props.page === 4 ? "active" : null}`}>

            <div className='form-banner'>
                <span>Contactame</span>
                <p>
                Completa el formulario y estaremos en contacto, tambi√©n puede contactarme en mis redes sociales.
                </p>
                <img src='https://static.vecteezy.com/system/resources/thumbnails/004/579/307/small_2x/programmer-working-on-computer-with-lots-of-data-free-vector.jpg' alt=''/>
            </div>

            <div className='form-box'>
                <span>Tu nombre</span>
                <div>
                    <svg fill="none" viewBox="0 0 24 24" strokeWidth={1.5} stroke="currentColor" className="w-6 h-6">
                        <path strokeLinecap="round" strokeLinejoin="round" d="M15.75 6a3.75 3.75 0 11-7.5 0 3.75 3.75 0 017.5 0zM4.501 20.118a7.5 7.5 0 0114.998 0A17.933 17.933 0 0112 21.75c-2.676 0-5.216-.584-7.499-1.632z" />
                    </svg>
                    <input 
                    type='text'
                    name='nombre'
                    value={value.nombre}
                    autoComplete='off'
                    onChange={handleChange}
                    />
                </div>


                <span>Correo</span>
                <input 
                type='text'
                name='correo'
                value={value.correo}
                autoComplete='off'
                onChange={handleChange}
                />

                <span>Mensaje</span>
                <textarea 
                type='text'
                name='mensaje'
                value={value.mensaje}
                autoComplete='off'
                onChange={handleChange}
                />
            </div>
        </section>
    );
}

export {Contact};
